package Assignment5;

public class ArithmeticAssignment {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// create variables
	    int a = 8;
	    int var;

	    // assign value using =
	    var = a;
	    System.out.println("var using =: " + var);

	    // assign value using =+
	    var += a;
	    System.out.println("var using +=: " + var);

	    // assign value using =*
	    var *= a;
	    System.out.println("var using *=: " + var);
	    // assign value using =*
	    var /= a;
	    System.out.println("var using /=: " + var);
	    // assign value using =*
	    var %= a;
	    System.out.println("var using %=: " + var);
	 
	    
	  }
	
}
